/*
 * This is the primary high-level API for the project. In this folder there should be:
 * A class called InsightFacade, this should be in a file called InsightFacade.ts.
 * You should not change this interface at all or the test suite will not work.
 */

export enum InsightDatasetKind {
	Sections = "sections",
	Rooms = "rooms",
}

export interface InsightDataset {
	id: string;
	kind: InsightDatasetKind;
	numRows: number;
}

export type InsightResult = Record<string, string | number>;

export class InsightError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, InsightError);
	}
}

export class NotFoundError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, NotFoundError);
	}
}

export class ResultTooLargeError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, ResultTooLargeError);
	}
}

export interface IInsightFacade {
	/**
	 * Add a dataset to insightUBC.
	 *
	 * @param id  The id of the dataset being added. Follows the format /^[^_]+$/
	 * @param content  The base64 content of the dataset. This content should be in the form of a serialized zip file.
	 * @param kind  The kind of the dataset
	 *
	 * @return Promise <string[]>
	 *
	 * The promise should fulfill on a successful add, reject for any failures.
	 * The promise should fulfill with a string array,
	 * containing the ids of all currently added datasets upon a successful add.
	 * The promise should reject with an InsightError describing the error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 * If id is the same as the id of an already added dataset, the dataset should be rejected and not saved.
	 *
	 * After receiving the dataset, it should be processed into a data structure of
	 * your design. The processed data structure should be persisted to disk; your
	 * system should be able to load this persisted value into memory for answering
	 * queries.
	 *
	 * Ultimately, a dataset must be added or loaded from disk before queries can
	 * be successfully answered.
	 */
	addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]>;

	/**
	 * Remove a dataset from insightUBC.
	 *
	 * @param id  The id of the dataset to remove. Follows the format /^[^_]+$/
	 *
	 * @return Promise <string>
	 *
	 * The promise should fulfill upon a successful removal, reject on any error.
	 * Attempting to remove a dataset that hasn't been added yet counts as an error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 *
	 * The promise should fulfill the id of the dataset that was removed.
	 * The promise should reject with a NotFoundError (if a valid id was not yet added)
	 * or an InsightError (invalid id or any other source of failure) describing the error.
	 *
	 * This will delete both disk and memory caches for the dataset for the id meaning
	 * that subsequent queries for that id should fail unless a new addDataset happens first.
	 */
	removeDataset(id: string): Promise<string>;

	/**
	 * Perform a query on insightUBC.
	 *
	 * @param query  The query to be performed.
	 *
	 * If a query is incorrectly formatted, references a dataset not added (in memory or on disk),
	 * or references multiple datasets, it should be rejected.
	 *
	 * @return Promise <InsightResult[]>
	 *
	 * The promise should fulfill with an array of results.
	 * The promise should reject with a ResultTooLargeError (if the query returns too many results)
	 * or an InsightError (for any other source of failure) describing the error.
	 */
	performQuery(query: unknown): Promise<InsightResult[]>;

	/**
	 * List all currently added datasets, their types, and number of rows.
	 *
	 * @return Promise <InsightDataset[]>
	 * The promise should fulfill an array of currently added InsightDatasets, and will only fulfill.
	 */
	listDatasets(): Promise<InsightDataset[]>;
}

import {
	IInsightFacade,
	InsightDataset,
	InsightDatasetKind,
	InsightError,
	InsightResult,
	ResultTooLargeError,
	NotFoundError,
} from "./IInsightFacade";
import DatasetManager from "./DatasetManager";
import QueryChecker, { Query } from "./QueryChecker"; // Import Query interface
import SectionQueryRunner from "./SectionQueryRunner";
import JSZip from "jszip";
import QuerySingleIdChecker from "./QuerySingleIdChecker";
import RoomQueryRunner from "./RoomQueryRunner";

export default class InsightFacade implements IInsightFacade {
	private datasetManager: DatasetManager;
	private isInitialized: boolean; // Flag to track if initialization has been completed
	private queryChecker: QueryChecker;
	private sectionQueryRunner: SectionQueryRunner;
	private querySingleIdChecker: QuerySingleIdChecker;
	private roomQueryRunner: RoomQueryRunner;

	constructor() {
		this.datasetManager = new DatasetManager();
		this.isInitialized = false; // Ensure the initialized flag is set to false initially
		this.sectionQueryRunner = new SectionQueryRunner(this.datasetManager);
		this.roomQueryRunner = new RoomQueryRunner(this.datasetManager);
		this.queryChecker = new QueryChecker();
		this.querySingleIdChecker = new QuerySingleIdChecker();
	}

	public async addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]> {
		// Validate dataset ID and kind
		await this.datasetManager.validateID(id);
		await this.datasetManager.idExisted(id); // Check if the ID already exists
		await this.datasetManager.validateKind(kind);
		try {
			// Initialize dataset manager if not already initialized
			if (!this.isInitialized) {
				await this.datasetManager.initialize();
				this.isInitialized = true; // Set initialized to true after successful initialization
			}
			const unzipped = await new JSZip().loadAsync(content, { base64: true });
			let parsedEntries: any[];

			if (kind === InsightDatasetKind.Sections) {
				// const folderName = "courses/";
				// const courseFolder = unzipped.folder(folderName);
				// if (courseFolder === null) {
				// 	throw new InsightError("No courses folder exists");
				// }
				// // Parse sections from the course folder
				// parsedEntries = await this.datasetManager.parseSectionsJson(courseFolder);
				parsedEntries = await this.datasetManager.parseSections(unzipped);
			} else {
				// kind === InsightDatasetKind.Rooms
				// const indexFile = Object.values(unzipped.files).find(file => file.name.endsWith('index.htm'));
				// if (!indexFile) {
				// 	throw new InsightError("No index.htm file exists");
				// }
				// // Extract the folder path correctly
				// const folderPath = indexFile.name.substring(0, indexFile.name.lastIndexOf('/') + 1);
				// // Get the folder JSZip object
				// const folder = unzipped.folder(folderPath);
				// if (!folder) {
				// 	throw new InsightError(`Folder not found: ${folderPath}`);
				// }
				// // Parse the rooms HTML
				// parsedEntries = await this.datasetManager.parseRoomsHTML(folder, indexFile);
				parsedEntries = await this.datasetManager.parseRooms(unzipped);
			}
			if (parsedEntries.length === 0) {
				throw new InsightError("No valid sections found in dataset");
			}
			// Write the dataset to disk and update internal maps
			await this.datasetManager.writeDatasetToZip(id, kind, parsedEntries.length, unzipped);
			this.datasetManager.setDatasetMaps(id, kind, parsedEntries);
			// this.datasetManager.printEntries();//to do
		} catch (_) {
			// Log error for debugging and throw a general error
			//  console.error(`Failed to add dataset: ${err}`);
			throw new InsightError("Failed to add dataset");
			// throw err;
		}

		return this.datasetManager.getDatasetIDs(); // Return the list of dataset IDs
	}

	public async performQuery(query: unknown): Promise<InsightResult[]> {
		// Initialize the dataset manager if it hasn't been initialized yet
		if (!this.isInitialized) {
			await this.datasetManager.initialize();
			this.isInitialized = true;
		}

		try {
			// Validate the query structure using the query validator
			if (!this.queryChecker.validateQueryFormat(query)) {
				throw new InsightError("Invalid query format");
			}

			// Assert that the query is of type Query at this point
			const queryObject = query as Query;

			// Ensure the query references exactly one dataset
			if (!this.querySingleIdChecker.checkSingleDataset(queryObject)) {
				throw new InsightError("Query must reference exactly one dataset.");
			}

			return this.queryRunner(queryObject);
		} catch (error) {
			if (error instanceof InsightError || error instanceof ResultTooLargeError) {
				throw error;
			} else {
				throw new InsightError("Failed to perform query");
			}
		}
	}

	public async queryRunner(queryObject: Query): Promise<InsightResult[]> {
		// Retrieve available datasets and check if the required dataset exists
		const datasetMap = this.datasetManager.getDatasets();
		const datasetId = this.queryChecker.extractDatasetId(queryObject);
		if (!datasetMap.has(datasetId)) {
			throw new InsightError(`Dataset with id "${datasetId}" does not exist.`);
		}

		const datasetKind = this.datasetManager.getKind(datasetId);

		// Ensure the dataset is of the correct kind
		if (datasetKind !== InsightDatasetKind.Sections && datasetKind !== InsightDatasetKind.Rooms) {
			throw new InsightError(`Invalid dataset kind for dataset id ${datasetId}`);
		}

		// Select the appropriate query runner based on the dataset kind
		// Determine if the dataset kind is 'Sections'
		const isSectionsKind = datasetKind === InsightDatasetKind.Sections;

		// Assign the correct query runner based on the dataset kind
		let queryRunner;
		if (isSectionsKind) {
			queryRunner = this.sectionQueryRunner;
		} else {
			queryRunner = this.roomQueryRunner;
		}

		// Execute the query using the selected query runner
		const queryResults = await queryRunner.execute(queryObject);

		// Ensure that the result set is within the acceptable size limit
		const maxResults = 5000;
		if (queryResults.length > maxResults) {
			throw new ResultTooLargeError();
		}
		return queryResults;
	}

	public async removeDataset(id: string): Promise<string> {
		// Initialize dataset manager if not already initialized
		if (!this.isInitialized) {
			await this.datasetManager.initialize();
			this.isInitialized = true;
		}

		// Validate the dataset ID
		await this.datasetManager.validateID(id);

		try {
			// Check if the dataset exists before removing
			await this.datasetManager.idExisted(id);
			return Promise.reject(new NotFoundError());
		} catch (_) {
			// Remove dataset from disk and internal maps
			await this.datasetManager.removeDatasetFromFacadeAndDisk(id);
		}

		return Promise.resolve(id); // Return the removed dataset ID
	}

	public async listDatasets(): Promise<InsightDataset[]> {
		// Initialize dataset manager if not already initialized
		if (!this.isInitialized) {
			await this.datasetManager.initialize();
			this.isInitialized = true;
		}

		// Return the list of available datasets
		// console.log(this.datasetManager.getInsightDataset());
		return this.datasetManager.getInsightDataset();
	}
}

import JSZip from "jszip";
import fs from "fs-extra";
import path from "node:path";
import Section from "./Section";
import Room from "./Room";
import { InsightDataset, InsightDatasetKind, InsightError } from "./IInsightFacade"; // Adjust the imports as needed
import * as parse5 from "parse5";

type DatasetEntry = Section | Room;

export default class DatasetManager {
	private datasetsEntries: Map<string, DatasetEntry[]> = new Map<string, DatasetEntry[]>();
	private datasetsKinds: Map<string, [InsightDatasetKind, number]> = new Map<string, [InsightDatasetKind, number]>();
	private folderPath = path.join(__dirname, "../../data");

	public async initialize(): Promise<void> {
		const idsAndKinds = await this.readIdsFromFile();
		await this.processStoredZipFiles(idsAndKinds);
	}

	public getDatasets(): Map<string, DatasetEntry[]> {
		return this.datasetsEntries;
	}

	public getKind(id: string): InsightDatasetKind | undefined {
		const kindAndRowNum = this.datasetsKinds.get(id);
		if (kindAndRowNum) {
			return kindAndRowNum[0];
		}
		return undefined;
	}

	// Setter for datasetsEntries
	public setDatasetMaps(id: string, kind: InsightDatasetKind, entries: DatasetEntry[]): void {
		this.datasetsEntries.set(id, entries);
		this.datasetsKinds.set(id, [kind, entries.length]);
	}

	// Getter for datasetsEntries IDS
	public getDatasetIDs(): string[] {
		return Array.from(this.datasetsEntries.keys());
	}

	public getInsightDataset(): InsightDataset[] {
		const InsightDatasets: InsightDataset[] = [];
		const ids: string[] = this.getDatasetIDs();
		for (const id of ids) {
			const kindAndRowNum: [InsightDatasetKind, number] | undefined = this.datasetsKinds.get(id);
			const kind: InsightDatasetKind = kindAndRowNum![0];
			const numRows: number = kindAndRowNum![1];
			InsightDatasets.push({ id, kind, numRows });
		}
		return InsightDatasets;
	}

	public async readIdsFromFile(): Promise<[string, [InsightDatasetKind, number]][]> {
		let parsedData: [string, [InsightDatasetKind, number]][] = [];
		try {
			await fs.ensureDir(path.join(__dirname, "../../data"));
			const idFilePath = path.join(__dirname, "../../data/id_log.json");
			const fileContent = await fs.readFile(idFilePath, "utf8");
			parsedData = JSON.parse(fileContent);
			return parsedData;
		} catch (_) {
			return parsedData;
		}
	}

	public async processStoredZipFiles(idsAndKinds: [string, [InsightDatasetKind, number]][]): Promise<void> {
		await fs.ensureDir(this.folderPath);
		const promises: Promise<DatasetEntry[]>[] = idsAndKinds.map(async (idAndKind) =>
			(async (): Promise<DatasetEntry[]> => {
				const zip = new JSZip();
				const buffer = await fs.readFile(path.join(this.folderPath, `${idAndKind[0]}.zip`));
				const content = buffer.toString("base64");
				const unzipped = await zip.loadAsync(content, { base64: true });
				if (idAndKind[1][0] === InsightDatasetKind.Sections) {
					const sections = await this.parseSections(unzipped);
					return sections;
				} else {
					const rooms = await this.parseRooms(unzipped);
					return rooms;
				}
			})()
		);
		const arrayOfSectionsArray: DatasetEntry[][] = await Promise.all(promises);
		for (let i = 0; i < idsAndKinds.length; i++) {
			if (!this.datasetsEntries.has(idsAndKinds[i][0])) {
				// check if id exists
				this.datasetsEntries.set(idsAndKinds[i][0], arrayOfSectionsArray[i]);
				this.datasetsKinds.set(idsAndKinds[i][0], [idsAndKinds[i][1][0], idsAndKinds[i][1][1]]);
			}
		}
	}

	public async parseRooms(unzipped: JSZip): Promise<Room[]> {
		const indexFile = Object.values(unzipped.files).find((file) => file.name.endsWith("index.htm"));
		if (!indexFile) {
			throw new InsightError("No index.htm file exists");
		}
		// Extract the folder path correctly
		const folderPath = indexFile.name.substring(0, indexFile.name.lastIndexOf("/") + 1);
		// Get the folder JSZip object
		const folder = unzipped.folder(folderPath);
		if (!folder) {
			throw new InsightError(`Folder not found: ${folderPath}`);
		}
		// Parse the rooms HTML
		const rooms = await this.parseRoomsHTML(folder, indexFile);
		return rooms;
	}

	public async parseSections(unzipped: JSZip): Promise<Section[]> {
		const courseFolder = unzipped.folder("courses/");
		if (courseFolder === null) {
			throw new Error(`No courses folder found in the zip file`);
		}
		const sections = await this.parseSectionsJson(courseFolder);
		return sections;
	}

	public async validateID(id: string): Promise<void> {
		if (id === "" || id === " " || id.includes("_")) {
			throw new InsightError("Invalid ID string");
		}
	}

	public async idExisted(id: string): Promise<void> {
		if (this.datasetsEntries.has(id)) {
			throw new InsightError("ID is used");
		}
	}

	public async validateKind(kind: InsightDatasetKind): Promise<void> {
		if (kind !== InsightDatasetKind.Sections && kind !== InsightDatasetKind.Rooms) {
			throw new InsightError("Invalid Kind");
		}
	}

	public async writeDatasetToZip(
		id: string,
		kind: InsightDatasetKind,
		rowNum: number,
		courseFolder: JSZip
	): Promise<void> {
		try {
			await fs.ensureDir(this.folderPath);
			const filePath = path.join(this.folderPath, `${id}.zip`);
			const zipContent = await courseFolder.generateAsync({ type: "nodebuffer" });
			await this.updateIdToJsonFile(id, kind, rowNum);
			await fs.writeFile(filePath, zipContent);
		} catch (_) {
			throw new InsightError("Failed to save .zip file");
		}
	}

	public async parseSectionsJson(courseFolder: JSZip): Promise<Section[]> {
		const promises: Promise<Section[]>[] = [];
		courseFolder.forEach((_, file: JSZip.JSZipObject) => {
			if (!file.dir && !file.name.startsWith("courses/.") && file.name.startsWith("courses/")) {
				promises.push(
					(async (): Promise<Section[]> => {
						try {
							const fileContent = await file.async("string");
							const parsedData = JSON.parse(fileContent);
							if (parsedData.result === undefined) {
								throw new InsightError("Result is undefined");
							}
							// return this.validateAndExtractSectionQueryableFields(parsedData.result);
							return Section.validateAndExtract(parsedData.result);
						} catch (err) {
							// console.error(`Error parsing file ${file.name}:`, err);
							if (err) {
								//
							}
							const empty: Section[] = [];
							return Promise.resolve(empty);
						}
					})()
				);
			}
		});
		const sectionsArray = await Promise.all(promises);
		return sectionsArray.flat();
	}

	public async removeDatasetFromFacadeAndDisk(id: string): Promise<void> {
		await fs.ensureDir(this.folderPath);
		const filePath = path.join(this.folderPath, `${id}.zip`);
		fs.unlink(filePath, (err) => {
			if (err) {
				//
			}
		});
		await this.updateIdToJsonFile(id, null, -1);
		this.datasetsKinds.delete(id);
		this.datasetsEntries.delete(id);
	}

	private async updateIdToJsonFile(id: string, kind: InsightDatasetKind | null, rowNum: number): Promise<void> {
		const filePath = path.join(__dirname, "../../data/id_log.json");
		let parsedData: [string, [InsightDatasetKind, number]][] = await this.readIdsFromFile();
		if (kind !== null) {
			parsedData.push([id, [kind, rowNum]]);
		} else {
			parsedData = parsedData.filter((data) => data[0] !== id);
		}

		try {
			const two = 2;
			await fs.writeFile(filePath, JSON.stringify(parsedData, null, two), "utf8");
		} catch (_) {
			throw new InsightError("Failed to updateIdToJsonFile");
		}
	}

	public async parseRoomsHTML(unzipped: JSZip, indexFile: JSZip.JSZipObject | undefined): Promise<Room[]> {
		if (indexFile === undefined) {
			return Promise.resolve([]);
		}
		const indexContent = await indexFile.async("string");
		const parsedIndex = parse5.parse(indexContent);

		const buildingsTable = this.findValidTable(parsedIndex);
		if (buildingsTable === null) {
			throw new InsightError("No building table");
		}

		const promises: Promise<Room[]>[] = this.extractBuildingPromises(buildingsTable, unzipped);

		// Use Promise.all() to resolve all room extraction promises concurrently
		const buildings = (await Promise.all(promises)).flat();

		return buildings;
	}

	private findValidTable(document: any): any | null {
		// Recursive function to traverse the HTML tree and find the table with <td> elements of specific classes
		return this.traverseTable(document);
	}

	private traverseTable(node: any): any | null {
		if (node.nodeName === "table") {
			const tbody = node.childNodes.find((child: any) => child.nodeName === "tbody");
			if (tbody && this.hasRequiredBuildingTdElements(tbody)) {
				return node; // Found a valid table
			}
		}
		if (node.childNodes) {
			for (const child of node.childNodes) {
				const result = this.traverseTable(child);
				if (result) {
					return result;
				}
			}
		}
		return null;
	}

	private hasRequiredBuildingTdElements(tbody: any): boolean {
		const requiredClasses = new Set([
			"views-field views-field-title",
			"views-field views-field-field-building-address",
		]);

		const tdElements = tbody.childNodes
			.filter((node: any) => node.nodeName === "tr")
			.flatMap((tr: any) => tr.childNodes.filter((td: any) => td.nodeName === "td"));

		// Collect the class values from the <td> elements
		const tdClasses = tdElements.map((td: any) => td.attrs?.find((attr: any) => attr.name === "class")?.value || "");

		// Check if the required classes are present
		for (const requiredClass of requiredClasses) {
			if (!tdClasses.includes(requiredClass)) {
				return false;
			}
		}
		return true;
	}

	private extractBuildingPromises(buildingsTable: any, unzipped: JSZip): Promise<Room[]>[] {
		const promises: Promise<Room[]>[] = [];
		const tbody = buildingsTable.childNodes.find((child: any) => child.nodeName === "tbody");
		if (tbody.childNodes) {
			for (const child of tbody.childNodes) {
				if (child.nodeName === "tr") {
					promises.push(this.createBuildingPromise(child, unzipped));
				}
			}
		}
		return promises;
	}

