	// 	const parsedBuilding = parse5.parse(await buildingFile.async("string"));
	// 	console.log("Successfully processed building:", buildingFullName);
	//
	// 	return Promise.resolve(Room.extractRooms(parsedBuilding, buildingAddress, buildingShortName, buildingFullName));
	// }

	private async createBuildingPromise(tr: any, unzipped: JSZip): Promise<Room[]> {
		let fileAddress: string | undefined;
		let buildingFullName: string | undefined;
		let buildingShortName: string | undefined;
		let buildingAddress: string | undefined;
		// Loop through <td> elements to extract relevant data
		for (const childTd of tr.childNodes) {
			if (childTd.nodeName === "td") {
				const tdClass = childTd.attrs.find((attr: any) => attr.name === "class")?.value;

				switch (tdClass) {
					case "views-field views-field-nothing":
						fileAddress = this.getLinkHref(childTd);
						break;

					case "views-field views-field-field-building-code":
						buildingShortName = this.getTextContent(childTd);
						break;

					case "views-field views-field-title":
						buildingFullName = this.getTextContent(childTd);
						break;

					case "views-field views-field-field-building-address":
						buildingAddress = this.getTextContent(childTd);
						break;
				}
			}
		}
		// Validate extracted data
		if (!fileAddress || !buildingFullName || !buildingShortName || !buildingAddress) {
			throw new InsightError("Invalid building data");
		}

		// Remove "./" prefix from fileAddress
		const cleanedPath = fileAddress.replace("./", "");

		// Locate the building file in the unzipped JSZip object
		const buildingFile = unzipped.file(cleanedPath);
		if (!buildingFile) {
			throw new InsightError(`Building file not found: ${cleanedPath}`);
		}

		// Read and parse the building file
		const parsedBuilding = parse5.parse(await buildingFile.async("string"));

		// Extract and return room information
		return Promise.resolve(Room.extractRooms(parsedBuilding, buildingAddress, buildingShortName, buildingFullName));
	}

	// Helper to extract the href value from an <a> tag inside a <td>
	private getLinkHref(td: any): string | undefined {
		const anchor = td.childNodes.find((child: any) => child.nodeName === "a");
		return anchor?.attrs.find((attr: any) => attr.name === "href")?.value;
	}

	private getTextContent(node: any): string {
		if (!node) {
			return "";
		}
		if (node.nodeName === "#text") {
			return node.value.trim(); // Extract text directly
		}
		// If the node has child nodes, recursively extract their text content
		return node.childNodes
			?.map((child: any) => this.getTextContent(child))
			.join(" ")
			.trim();
	}
}

export default class Section {
	private readonly uuid: string;
	private readonly id: string;
	private readonly title: string;
	private readonly instructor: string;
	private readonly dept: string;
	private readonly year: number;
	private readonly avg: number;
	private readonly pass: number;
	private readonly fail: number;
	private readonly audit: number;

	constructor(
		uuid: string,
		id: string,
		title: string,
		instructor: string,
		dept: string,
		year: number,
		avg: number,
		pass: number,
		fail: number,
		audit: number
	) {
		this.uuid = uuid;
		this.id = id;
		this.title = title;
		this.instructor = instructor;
		this.dept = dept;
		this.year = year;
		this.avg = avg;
		this.pass = pass;
		this.fail = fail;
		this.audit = audit;
	}

	// Getter for uuid
	public getUuid(): string {
		return this.uuid;
	}

	// Getter for id
	public getId(): string {
		return this.id;
	}

	// Getter for title
	public getTitle(): string {
		return this.title;
	}

	// Getter for instructor
	public getInstructor(): string {
		return this.instructor;
	}

	// Getter for dept (department)
	public getDept(): string {
		return this.dept;
	}

	// Getter for year
	public getYear(): number {
		return this.year;
	}

	// Getter for avg (average)
	public getAvg(): number {
		return this.avg;
	}

	// Getter for pass
	public getPass(): number {
		return this.pass;
	}

	// Getter for fail
	public getFail(): number {
		return this.fail;
	}

	// Getter for audit
	public getAudit(): number {
		return this.audit;
	}

	// originally from DatasetManager
	public static validateAndExtract(data: any[]): Section[] {
		const sections: Section[] = [];

		data.forEach((singleSection) => {
			const isValid: boolean =
				typeof singleSection.id === "number" &&
				typeof singleSection.Course === "string" &&
				typeof singleSection.Title === "string" &&
				typeof singleSection.Professor === "string" &&
				typeof singleSection.Subject === "string" &&
				typeof singleSection.Year === "string" &&
				typeof singleSection.Avg === "number" &&
				typeof singleSection.Pass === "number" &&
				typeof singleSection.Fail === "number" &&
				typeof singleSection.Audit === "number";

			if (isValid) {
				const section = new Section(
					singleSection.id.toString(),
					singleSection.Course,
					singleSection.Title,
					singleSection.Professor,
					singleSection.Subject,
					parseInt(singleSection.Year, 10),
					singleSection.Avg,
					singleSection.Pass,
					singleSection.Fail,
					singleSection.Audit
				);
				sections.push(section);
			}
		});

		return sections;
	}
}

import http from "http";

// Interface representing the response from the geolocation API
export interface GeoResponse {
	lat?: number; // Latitude of the location
	lon?: number; // Longitude of the location
	error?: string; // Error message, if any
}

export default class Room {
	private readonly fullname: string;
	private readonly shortname: string;
	private readonly number: string;
	private readonly name: string;
	private readonly address: string;
	private readonly lat: number;
	private readonly lon: number;
	private readonly seats: number;
	private readonly type: string;
	private readonly furniture: string;
	private readonly href: string;

	// Constructor to initialize the Room object
	constructor(
		fullname: string,
		shortname: string,
		number: string,
		address: string,
		lat: number,
		lon: number,
		seats: number,
		type: string,
		furniture: string,
		href: string
	) {
		this.fullname = fullname;
		this.shortname = shortname;
		this.number = number;
		this.name = `${shortname}_${number}`; // Derived from shortname and number
		this.address = address;
		this.lat = lat;
		this.lon = lon;
		this.seats = seats;
		this.type = type;
		this.furniture = furniture;
		this.href = href;
	}

	// Getters for each property
	public getFullname(): string {
		return this.fullname;
	}

	public getShortname(): string {
		return this.shortname;
	}

	public getNumber(): string {
		return this.number;
	}

	public getName(): string {
		return this.name;
	}

	public getAddress(): string {
		return this.address;
	}

	public getLat(): number {
		return this.lat;
	}

	public getLon(): number {
		return this.lon;
	}

	public getSeats(): number {
		return this.seats;
	}

	public getType(): string {
		return this.type;
	}

	public getFurniture(): string {
		return this.furniture;
	}

	public getHref(): string {
		return this.href;
	}

	public static async extractRooms(
		parsedBuilding: any,
		buildingAddress: string,
		buildingShortName: string,
		buildingFullName: string
	): Promise<Room[]> {
		const table = this.findValidTable(parsedBuilding);
		const tbody = table?.childNodes.find((child: any) => child.nodeName === "tbody");

		if (!tbody) {
			return [];
		}

		// Create an array of promises for each room extraction
		const roomPromises = tbody.childNodes
			.filter((child: any) => child.nodeName === "tr")
			.map(async (childTr: any) =>
				this.extractRoomFromTr(childTr, buildingAddress, buildingShortName, buildingFullName)
			);

		// Wait for all promises to resolve
		const resolvedRooms = await Promise.all(roomPromises);

		// Filter out any `null` results and return the valid rooms
		return resolvedRooms.filter((room) => room !== null) as Room[];
	}

	private static async extractRoomFromTr(
		tr: any,
		buildingAddress: string,
		buildingShortName: string,
		buildingFullName: string
	): Promise<Room | null> {
		const roomDetails = this.extractRoomDetails(tr);

		// Fetch geolocation
		const geolocation = await this.getGeolocation(buildingAddress);
		if (!geolocation) {
			return null;
		} else if (!geolocation.lat || !geolocation.lon) {
			return null;
		}

		// If any required room details are missing, return null
		if (roomDetails === null) {
			return null;
		}

		// Create and return the Room object
		return new Room(
			buildingFullName,
			buildingShortName,
			roomDetails.roomNumber,
			buildingAddress,
			geolocation.lat,
			geolocation.lon,
			roomDetails.capacity,
			roomDetails.type,
			roomDetails.furniture,
			roomDetails.href
		);
	}

	private static extractRoomDetails(
		tr: any
	): { roomNumber: string; capacity: number; furniture: string; type: string; href: string } | null {
		const details: Record<string, any> = {};
		for (const childTd of tr.childNodes) {
			if (childTd.nodeName === "td") {
				const tdClass = childTd.attrs.find((attr: any) => attr.name === "class")?.value;
				switch (tdClass) {
					case "views-field views-field-field-room-number":
						details.roomNumber = this.getLinkText(childTd);
						break;
					case "views-field views-field-field-room-capacity":
						details.capacity = parseInt(this.getTextContent(childTd), 10);
						break;
					case "views-field views-field-field-room-furniture":
						details.furniture = this.getTextContent(childTd);
						break;
					case "views-field views-field-field-room-type":
						details.type = this.getTextContent(childTd);
						break;
					case "views-field views-field-nothing":
						details.href = this.getLinkHref(childTd);
						break;
				}
			}
		}

		return typeof details.roomNumber === "undefined" ||
			typeof details.capacity === "undefined" ||
			typeof details.furniture === "undefined" ||
			typeof details.type === "undefined" ||
			typeof details.href === "undefined"
			? null
			: (details as { roomNumber: string; capacity: number; furniture: string; type: string; href: string });
	}

	// Helper function to fetch geolocation
	private static async getGeolocation(buildingAddress: string): Promise<GeoResponse | null> {
		try {
			const geolocation = await this.fetchGeolocation(buildingAddress);
			if (geolocation.lat !== undefined && geolocation.lon !== undefined) {
				return geolocation;
			}
			return null;
		} catch {
			return null;
		}
	}

	// Helper to extract href from <a> tag inside <td>
	private static getLinkHref(td: any): string | undefined {
		const anchor = td.childNodes.find((child: any) => child.nodeName === "a");
		return anchor?.attrs.find((attr: any) => attr.name === "href")?.value;
	}

	// Helper to extract text from <a> tag inside <td>
	private static getLinkText(td: any): string {
		const anchor = td.childNodes.find((child: any) => child.nodeName === "a");
		return (
			anchor?.childNodes
				.filter((child: any) => child.nodeName === "#text")
				.map((textNode: any) => textNode.value.trim())
				.join(" ") || ""
		);
	}

	// Helper to extract text content from <td>
	private static getTextContent(td: any): string {
		return td.childNodes
			.filter((child: any) => child.nodeName === "#text")
			.map((textNode: any) => textNode.value.trim())
			.join(" ");
	}

	// Helper function to fetch the geolocation of an address
	private static async fetchGeolocation(address: string): Promise<GeoResponse> {
		// Encode the address using encodeURIComponent
		const encodedAddress = encodeURIComponent(address);
		const url = `http://cs310.students.cs.ubc.ca:11316/api/v1/project_team051/${encodedAddress}`;

		return new Promise((resolve, reject) => {
			http
				.get(url, (res) => {
					let data = "";

					// Collect data chunks
					res.on("data", (chunk) => {
						data += chunk;
					});

					// Handle response completion
					res.on("end", () => {
						try {
							const geoResponse: GeoResponse = JSON.parse(data);
							resolve(geoResponse);
						} catch {
							reject(new Error("Failed to parse geolocation response"));
						}
					});
				})
				.on("error", () => {
					reject(new Error("HTTP request failed"));
				});
		});
	}

	private static findValidTable(document: any): any | null {
		// Recursive function to traverse the HTML tree and find the table with <td> elements of specific classes
		return this.traverseTable(document);
	}

	private static traverseTable(node: any): any | null {
		if (node.nodeName === "table") {
			const tbody = node.childNodes.find((child: any) => child.nodeName === "tbody");
			if (tbody && this.hasRequiredRoomTdElements(tbody)) {
				return node; // Found a valid table
			}
		}
		if (node.childNodes) {
			for (const child of node.childNodes) {
				const result = this.traverseTable(child);
				if (result) {
					return result;
				}
			}
		}
		return null;
	}

	private static hasRequiredRoomTdElements(tbody: any): boolean {
		const requiredClasses = new Set([
			"views-field views-field-field-room-number",
			"views-field views-field-field-room-capacity",
			"views-field views-field-field-room-furniture",
			"views-field views-field-field-room-type",
			"views-field views-field-nothing",
		]);
		const tdElements = tbody.childNodes
			.filter((node: any) => node.nodeName === "tr")
			.flatMap((tr: any) => tr.childNodes.filter((td: any) => td.nodeName === "td"));

		// Collect the class values from the <td> elements
		const tdClasses = tdElements.map((td: any) => td.attrs?.find((attr: any) => attr.name === "class")?.value || "");

		// Check if the required classes are present
		for (const requiredClass of requiredClasses) {
			if (!tdClasses.includes(requiredClass)) {
				return false;
			}
		}
		return true;
	}
}

import { InsightError } from "./IInsightFacade";
import TransformationValidator from "./TransformationValidator";

export interface Query {
	WHERE: any;
	OPTIONS: {
		COLUMNS: string[];
		ORDER?: any;
	};
	TRANSFORMATIONS?: {
		GROUP: string[];
		APPLY: Record<string, Record<string, string>>[];
	};
}

const validKeys = new Set([
	"avg",
	"pass",
	"fail",
	"audit",
	"year",
	"lat",
	"lon",
	"seats",
	"dept",
	"id",
	"instructor",
	"title",
	"uuid",
	"fullname",
	"shortname",
	"number",
	"name",
	"address",
	"type",
	"furniture",
	"href",
]);

export default class QueryValidator {
	private transformationValidator: TransformationValidator;

	constructor() {
		this.transformationValidator = new TransformationValidator(validKeys);
	}

	// Validate the basic format of the query object
	public validateQueryFormat(query: unknown): boolean {
		if (!this.isValidQueryObject(query)) {
			throw new InsightError("Invalid query format");
		}

		const queryObj = query as Query;
		this.checkUnexpectedKeys(queryObj, ["WHERE", "OPTIONS", "TRANSFORMATIONS"]);
		const datasetId = this.extractDatasetId(queryObj);

		if (queryObj.TRANSFORMATIONS) {
			this.transformationValidator.checkTransClause(queryObj.TRANSFORMATIONS, datasetId);
		}
		this.checkOptionsClause(queryObj.OPTIONS, datasetId, queryObj.TRANSFORMATIONS);
		this.checkWhereClause(queryObj.WHERE, datasetId);

		return true;
	}

	private checkUnexpectedKeys(obj: any, allowedKeys: string[]): void {
		for (const key in obj) {
			if (!allowedKeys.includes(key)) {
				throw new InsightError(`Excess keys in query: ${key} is not allowed`);
			}
		}
	}

	private isValidQueryObject(query: unknown): query is Query {
		return (
			typeof query === "object" &&
			query !== null &&
			"WHERE" in query &&
			"OPTIONS" in query &&
			typeof (query as any).OPTIONS === "object" &&
			(query as any).OPTIONS !== null &&
			Array.isArray((query as any).OPTIONS?.COLUMNS)
		);
	}

	public extractDatasetId(query: Query): string {
		for (const col of query.OPTIONS?.COLUMNS || []) {
			if (col.includes("_")) {
				return col.split("_")[0];
			}
		}
		if (query.TRANSFORMATIONS) {
			return this.transformationValidator.extractDatasetIdFromTransformation(query.TRANSFORMATIONS);
		}
		throw new InsightError("Could not extract dataset ID from COLUMNS or TRANSFORMATIONS.");
	}

	private checkOptionsClause(
		options: Query["OPTIONS"],
		datasetId: string,
		transformations?: Query["TRANSFORMATIONS"]
	): boolean {
		if (typeof options !== "object" || options === null) {
			throw new InsightError("OPTIONS must be a valid object");
		}

		if (transformations?.APPLY) {
			for (const applyRule of transformations.APPLY) {
				const applyKey = Object.keys(applyRule)[0];
				validKeys.add(applyKey);
			}
		}

		this.validateColumns(options.COLUMNS, datasetId);

		if ("ORDER" in options) {
			this.checkOrderClause(options.ORDER, options.COLUMNS);
		}

		return true;
	}

	private validateColumns(columns: string[], datasetId: string): void {
		if (!Array.isArray(columns) || columns.length === 0) {
			throw new InsightError("OPTIONS must contain a non-empty COLUMNS array.");
		}

		for (const key of columns) {
			if (validKeys.has(key)) {
				continue;
			}
			if (!key.includes("_")) {
				throw new InsightError(`Invalid key in COLUMNS: ${key}`);
			}

			const [keyId, field] = key.split("_");
			if (keyId !== datasetId) {
				throw new InsightError(`Key does not match dataset ID: ${key}`);
			}
			if (!validKeys.has(field)) {
				throw new InsightError(`Invalid field in COLUMNS: ${field}`);
			}
		}
	}

	private checkOrderClause(order: any, columns: string[]): boolean {
		if (typeof order === "string") {
			if (!columns.includes(order)) {
				throw new InsightError("ORDER key must be in COLUMNS.");
			}
		} else if (typeof order === "object") {
			const allowedOrderKeys = ["keys", "dir"];
			for (const key in order) {
				if (!allowedOrderKeys.includes(key)) {
					throw new InsightError(`Invalid key in ORDER: ${key}.`);
				}
			}

			if (!order.keys || !Array.isArray(order.keys) || order.keys.length === 0) {
				throw new InsightError("ORDER keys must be a non-empty array.");
			}
			for (const key of order.keys) {
				if (!columns.includes(key)) {
					throw new InsightError(`ORDER key ${key} must be in COLUMNS.`);
				}
			}
			if (!order.dir || !["UP", "DOWN"].includes(order.dir)) {
				throw new InsightError("ORDER dir must be 'UP' or 'DOWN'.");
			}
		} else {
			throw new InsightError("Invalid ORDER format.");
		}
		return true;
	}

	private checkWhereClause(WHERE: any, datasetId: string): boolean {
		if (Object.keys(WHERE).length === 0) {
			return true;
		}
		return this.checkFilter(WHERE, datasetId);
	}

	private checkFilter(filter: any, datasetId: string): boolean {
		if (typeof filter !== "object" || filter === null) {
			return false;
		}

		if ("AND" in filter || "OR" in filter) {
			const logic = filter.AND || filter.OR;
			if (!Array.isArray(logic) || logic.length === 0) {
				throw new InsightError("LOGIC must be a non-empty array");
			}
			return logic.every((f: any) => this.checkFilter(f, datasetId));
		}

		if ("NOT" in filter) {
			if (Object.keys(filter).length !== 1) {
				throw new InsightError("NOT must have exactly one key");
			}
			return this.checkFilter(filter.NOT, datasetId);
		}

		if ("GT" in filter || "LT" in filter || "EQ" in filter) {
			return this.checkMComparison(filter.GT || filter.LT || filter.EQ, datasetId);
		}

		if ("IS" in filter) {
			return this.checkSComparison(filter.IS, datasetId);
		}

		return false;
	}

	private checkMComparison(mcomp: any, datasetId: string): boolean {
		const keys = Object.keys(mcomp);
		if (keys.length !== 1) {
			throw new InsightError("MCOMPARISON must have exactly one key");
		}

		const key = keys[0];
		if (!key.includes("_")) {
			return false;
		}

		const [keyId, field] = key.split("_");
		if (keyId !== datasetId) {
			throw new InsightError(`Key does not match dataset ID: ${key}`);
		}
		if (!validKeys.has(field)) {
			return false;
		}

		const value = mcomp[key];
		if (typeof value !== "number") {
			throw new InsightError("GT, LT, EQ require numeric values");
		}

		return true;
	}

	private checkSComparison(scomp: any, datasetId: string): boolean {
		const keys = Object.keys(scomp);
		if (keys.length !== 1) {
			throw new InsightError("SCOMPARISON must have exactly one key");
		}

		const key = keys[0];
		if (!key.includes("_")) {
			return false;
		}

		const [keyId, field] = key.split("_");
		if (keyId !== datasetId) {
			throw new InsightError(`Key does not match dataset ID: ${key}`);
		}
		if (!validKeys.has(field)) {
			return false;
		}

		const value = scomp[key];
		if (typeof value !== "string") {
			throw new InsightError("IS operator requires string values");
		}

		return true;
	}
}

import { InsightError } from "./IInsightFacade";

export default class TransformationValidator {
	private validKeys: Set<string>;

	constructor(validKeys: Set<string>) {
		this.validKeys = validKeys;
	}

	public checkTransClause(transformations: any, datasetId: string): boolean {
		if (!transformations?.GROUP || !transformations?.APPLY) {
			throw new InsightError("TRANSFORMATIONS must contain GROUP and APPLY.");
		}

		// Ensure no extra keys in TRANSFORMATIONS
		const allowedKeys = ["GROUP", "APPLY"];
		for (const key in transformations) {
			if (!allowedKeys.includes(key)) {
				throw new InsightError(`Extra keys in TRANSFORMATIONS: ${key} is not allowed`);
			}
		}

		this.validateGroupClause(transformations.GROUP, datasetId);
		this.validateApplyClause(transformations.APPLY, datasetId);
		return true;
	}

	public extractDatasetIdFromTransformation(trans: any): string {
		if (Array.isArray(trans.GROUP)) {
			for (const groupKey of trans.GROUP) {
				if (typeof groupKey === "string" && groupKey.includes("_")) {
					return groupKey.split("_")[0];
				}
			}
		}

		if (Array.isArray(trans.APPLY)) {
			for (const applyRule of trans.APPLY) {
				if (typeof applyRule === "object" && applyRule !== null) {
					const applyKey = Object.values(applyRule)[0];
					if (typeof applyKey === "object" && applyKey !== null) {
						const targetField = Object.values(applyKey)[0];
						if (typeof targetField === "string" && targetField.includes("_")) {
							return targetField.split("_")[0];
						}
					}
				}
			}
		}

		throw new InsightError("Could not extract dataset ID from TRANSFORMATIONS.");
	}

	private validateGroupClause(group: string[], datasetId: string): void {
		if (!Array.isArray(group) || group.length === 0) {
			throw new InsightError("GROUP must be a non-empty array.");
		}
		for (const field of group) {
			if (typeof field !== "string" || !field.includes("_")) {
				throw new InsightError("Each GROUP entry must be a string in the format 'dataset_field'.");
			}
			const [keyId] = field.split("_");
			if (keyId !== datasetId) {
				throw new InsightError(`GROUP field ${field} does not match dataset ID: ${datasetId}.`);
			}
		}
	}

	private validateApplyClause(apply: Record<string, Record<string, string>>[], datasetId: string): void {
		if (!Array.isArray(apply)) {
			throw new InsightError("APPLY must be an array.");
		}

		const seenApplyKeys = new Set<string>();
		for (const applyRule of apply) {
			this.validateApplyRule(applyRule, datasetId);

			const applyKey = Object.keys(applyRule)[0];
			if (seenApplyKeys.has(applyKey)) {
				throw new InsightError(`Duplicate APPLY key: ${applyKey}`);
			}
			seenApplyKeys.add(applyKey);
		}
	}

	private validateApplyRule(applyRule: Record<string, Record<string, string>>, datasetId: string): void {
		const keys = Object.keys(applyRule);
		if (keys.length !== 1) {
			throw new InsightError("Each APPLY rule must have exactly one key.");
		}

		const applyKey = keys[0];
		const operationObject = applyRule[applyKey];

		// Check for underscores in applyKey
		if (applyKey.includes("_")) {
			throw new InsightError("APPLY key cannot contain underscores.");
		}

		if (typeof applyKey !== "string") {
			throw new InsightError("APPLY key must be a string.");
		}
		if (typeof operationObject !== "object" || operationObject === null) {
			throw new InsightError("Each APPLY rule must map to an operation object.");
		}

		const operationKeys = Object.keys(operationObject);
		if (operationKeys.length !== 1) {
			throw new InsightError("Each APPLY operation must contain exactly one operation.");
		}

		const operation = operationKeys[0];
		const targetField = operationObject[operation];

		if (!["MAX", "MIN", "AVG", "COUNT", "SUM"].includes(operation)) {
			throw new InsightError(`Invalid APPLY operation: ${operation}`);
		}
		if (typeof targetField !== "string" || !targetField.includes("_")) {
			throw new InsightError("APPLY operation target must be a string in the format 'dataset_field'.");
		}

		const [targetDatasetId] = targetField.split("_");
		if (targetDatasetId !== datasetId) {
			throw new InsightError(`APPLY target field ${targetField} does not match dataset ID: ${datasetId}.`);
		}
	}
}

import { InsightError, InsightResult } from "./IInsightFacade";
import DatasetManager from "./DatasetManager"; // Use DatasetManager for handling datasets
import Section from "./Section";
import Room from "./Room";
import Decimal from "decimal.js";
type DatasetEntry = Section | Room;

// Define a constant for the decimal precision
const DECIMAL_PRECISION = 2;

export default class SectionQueryRunner {
	private datasetMap: Map<string, DatasetEntry[]>;
	private datasetId!: string; // Declare datasetId with definite assignment

	constructor(datasetManager: DatasetManager) {
		this.datasetMap = datasetManager.getDatasets(); // Initialize datasets
	}

	// Execute the query and return the results
	public async execute(query: any): Promise<InsightResult[]> {
		this.datasetId = this.extractDatasetId(query);

		if (!this.datasetMap.has(this.datasetId)) {
			throw new InsightError(`Dataset with id "${this.datasetId}" does not exist.`);
		}

		const dataset = this.datasetMap.get(this.datasetId) as Section[];
		const filteredData = this.applyFilters(dataset, query.WHERE);

		// Validate columns against GROUP and APPLY if TRANSFORMATIONS is present
		if (query.TRANSFORMATIONS) {
			const groupKeys = new Set(query.TRANSFORMATIONS.GROUP);
			const applyKeys = new Set(query.TRANSFORMATIONS.APPLY.map((applyRule: any) => Object.keys(applyRule)[0]));
			for (const col of query.OPTIONS.COLUMNS) {
				if (!groupKeys.has(col) && !applyKeys.has(col)) {
					throw new InsightError(`Key ${col} in COLUMNS must be in GROUP or APPLY when TRANSFORMATIONS.`);
				}
			}
		}

		const transformedData: InsightResult[] = query.TRANSFORMATIONS
			? this.applyTransformations(filteredData, query.TRANSFORMATIONS)
			: (filteredData as unknown as InsightResult[]);

		return this.applyOptions(transformedData, query.OPTIONS);
	}

	// Extract dataset ID from query COLUMNS
	private extractDatasetId(query: any): string {
		// Check COLUMNS for dataset ID
		for (const col of query.OPTIONS?.COLUMNS || []) {
			if (col.includes("_")) {
				return col.split("_")[0];
			}
		}

		// If COLUMNS don't contain dataset ID, check GROUP keys in TRANSFORMATIONS
		for (const groupKey of query.TRANSFORMATIONS?.GROUP || []) {
			if (groupKey.includes("_")) {
				return groupKey.split("_")[0];
			}
		}

		throw new InsightError("Could not extract dataset ID from COLUMNS or TRANSFORMATIONS GROUP.");
	}

	// Apply the filter conditions specified in WHERE clause
	private applyFilters(data: Section[], where: any): Section[] {
		if (Object.keys(where).length === 0) {
			return data;
		}

		const filterFunction = this.createFilterFunction(where);
		return data.filter(filterFunction);
	}

	// Create a filter function based on WHERE conditions
	private createFilterFunction(where: any): (item: Section) => boolean {
		if (where.AND) {
			return this.handleAnd(where.AND);
		}
		if (where.OR) {
			return this.handleOr(where.OR);
		}
		if (where.NOT) {
			return this.handleNot(where.NOT);
		}
		if (where.GT || where.LT || where.EQ) {
			return this.handleComparison(where);
		}
		if (where.IS) {
			return this.handleIs(where.IS);
		}
		throw new InsightError("Invalid WHERE condition.");
	}

	// Handle AND logic for filters
	private handleAnd(andConditions: any[]): (item: Section) => boolean {
		const subFilters = andConditions.map((condition) => this.createFilterFunction(condition));
		return (item: Section) => subFilters.every((filterFunc) => filterFunc(item));
	}

	// Handle OR logic for filters
	private handleOr(orConditions: any[]): (item: Section) => boolean {
		const subFilters = orConditions.map((condition) => this.createFilterFunction(condition));
		return (item: Section) => subFilters.some((filterFunc) => filterFunc(item));
	}

	// Handle NOT logic for filters
	private handleNot(notCondition: any): (item: Section) => boolean {
		const subFilter = this.createFilterFunction(notCondition);
		return (item: Section) => !subFilter(item);
	}

	// Handle comparison operators like GT, LT, EQ
	private handleComparison(where: any): (item: Section) => boolean {
		const operator = where.GT ? "GT" : where.LT ? "LT" : "EQ";
		const comparison = where[operator];
		const key = Object.keys(comparison)[0];
		const value = comparison[key];

		const id = key.split("_")[0]; // Extract dataset ID part
		if (id !== this.datasetId) {
			throw new InsightError(`Key does not match dataset ID: ${key}`);
		}

		return (item: Section) => {
			const itemValue = this.getValueByKey(item, key);

			if (typeof itemValue !== "number" || typeof value !== "number") {
				throw new InsightError("Comparison requires numeric values.");
			}

			if (operator === "GT") {
				return itemValue > value;
			}
			if (operator === "LT") {
				return itemValue < value;
			}
			if (operator === "EQ") {
				return itemValue === value;
			}

			return false;
		};
	}

	// Handle the IS operator for string comparisons in the WHERE clause
	private handleIs(isCondition: any): (item: Section) => boolean {
		const key = Object.keys(isCondition)[0];
		const value = isCondition[key];

		// Extract dataset ID from the query key
		const id = key.split("_")[0];
		if (id !== this.datasetId) {
			throw new InsightError(`Key does not match dataset ID: ${key}`);
		}

		// Ensure the value provided to IS a string
		if (typeof value !== "string") {
			throw new InsightError("IS operator requires a string value.");
		}

		// Check if the wildcard pattern is invalid, throw InsightError if true
		if (this.hasInvalidWildcardPattern(value)) {
			throw new InsightError("Invalid wildcard pattern in IS operator.");
		}

		// Convert wildcard pattern into a regular expression for matching
		const regexPattern = this.buildRegexPattern(value);
		const regex = new RegExp(regexPattern);

		// Return a function that tests the string value from the Section object
		return (item: Section) => {
			const itemValue = this.getValueByKey(item, key);

			if (typeof itemValue !== "string") {
				throw new InsightError("IS operator requires string fields.");
			}

			return regex.test(itemValue);
		};
	}

	private hasInvalidWildcardPattern(value: string): boolean {
		// Skip validation if there’s no wildcard character.
		if (!value.includes("*")) {
			return false;
		}

		const firstIndex = value.indexOf("*");
		const lastIndex = value.lastIndexOf("*");
		// The pattern is only valid if the wildcard appears at the start or end
		return firstIndex !== 0 && lastIndex !== value.length - 1;
	}

	// Build regex pattern for handling wildcard in IS operator
	private buildRegexPattern(value: string): string {
		const specialChars = /[.+?^${}()|[\]\\]/g; // Exclude '*'
		const escapedValue = value.replace(specialChars, "\\$&");
		return `^${escapedValue.replace(/\*/g, ".*")}$`;
	}

	// Get value from Section or InsightResult based on key
	private getValueByKey(item: Section | InsightResult, key: string): any {
		// If item is already an InsightResult, return the value directly
		if (!(item instanceof Section)) {
			return item[key];
		}

		// Otherwise, it's a Section and we access its fields
		const [id, field] = key.split("_");
		if (id !== this.datasetId) {
			throw new InsightError(`Key does not match dataset ID: ${key}`);
		}
		switch (field) {
			case "uuid":
				return String(item.getUuid());
			case "id":
				return String(item.getId());
			case "title":
				return String(item.getTitle());
			case "instructor":
				return String(item.getInstructor());
			case "dept":
				return String(item.getDept());
			case "year":
				return this.checkYear(item);
			case "avg":
				return item.getAvg();
			case "pass":
				return item.getPass();
			case "fail":
				return item.getFail();
			case "audit":
				return item.getAudit();
			default:
				throw new InsightError(`Invalid column: ${key}`);
		}
	}

	// Adjust year based on the instructor field
	private checkYear(item: Section): number {
		if (item.getInstructor() === "") {
			const num = 1900;
			return num;
		}
		return item.getYear();
	}

	// Apply transformations for GROUP and APPLY
	private applyTransformations(data: Section[], transformations: any): InsightResult[] {
		const groupKeys = transformations.GROUP;
		const groupedData = this.groupBy(data, groupKeys);
		return this.applyAggregations(groupedData, transformations.APPLY, groupKeys);
	}

	// Group data by specified keys
	private groupBy(data: Section[], groupKeys: string[]): Map<string, Section[]> {
		const groupedData = new Map<string, Section[]>();

		data.forEach((item) => {
			const groupKey = groupKeys.map((key) => this.getValueByKey(item, key)).join("_");
			if (!groupedData.has(groupKey)) {
				groupedData.set(groupKey, []);
			}
			groupedData.get(groupKey)!.push(item);
		});

		return groupedData;
	}

	// Apply aggregation rules to each group
	private applyAggregations(
		groupedData: Map<string, Section[]>,
		applyRules: any[],
		groupKeys: string[]
	): InsightResult[] {
		const results: InsightResult[] = [];

		groupedData.forEach((group) => {
			const result: any = {};

			// Include group keys (like sections_title) in each result
			groupKeys.forEach((key) => {
				result[key] = this.getValueByKey(group[0], key);
			});

			for (const rule of applyRules) {
				const applyKey = Object.keys(rule)[0];
				const applyToken = rule[applyKey];
				const aggregationField = Object.values(applyToken)[0] as string;
				const aggregationType = Object.keys(applyToken)[0] as string;

				result[applyKey] = this.performAggregation(aggregationType, group, aggregationField);
			}

			results.push(result);
		});

		return results;
	}

	// Perform aggregation operations
	private performAggregation(type: string, group: Section[], field: string): number {
		const isNumeric = group.every((item) => {
			const value = this.getValueByKey(item, field);
			return typeof value === "number" && !isNaN(value);
		});

		if (!isNumeric) {
			throw new InsightError(`${type} requires a numeric field`);
		}
		switch (type) {
			case "MAX":
				return Math.max(...group.map((item) => this.getValueByKey(item, field) as number));
			case "MIN":
				return Math.min(...group.map((item) => this.getValueByKey(item, field) as number));
			case "AVG": {
				// Ensure each value is converted to a Decimal and add them up
				const total = group.reduce(
					(acc, item) => acc.add(new Decimal(this.getValueByKey(item, field) as number)),
					new Decimal(0)
				);

				// Calculate the average by dividing total by the number of items (numRows)
				const avg = total.toNumber() / group.length;

				// Round the result to two decimal places and cast it to a number
				return Number(avg.toFixed(DECIMAL_PRECISION));
			}

			case "SUM": {
				// Use Decimal for precision, and round the final result
				const total = group.reduce(
					(acc, item) => acc.add(new Decimal(this.getValueByKey(item, field) as number)),
					new Decimal(0)
				);
				return Number(total.toFixed(DECIMAL_PRECISION));
			}
			case "COUNT":
				return new Set(group.map((item) => this.getValueByKey(item, field))).size;
			default:
				throw new InsightError("Invalid aggregation type");
		}
	}

	// Apply options like COLUMNS and ORDER to the filtered or transformed data
	private applyOptions(data: Section[] | InsightResult[], options: any): InsightResult[] {
		if (!options.COLUMNS || !Array.isArray(options.COLUMNS)) {
			throw new InsightError("OPTIONS must contain COLUMNS array.");
		}

		const columns = options.COLUMNS;
		let result = data.map((item) => {
			const res: any = {};
			for (const col of columns) {
				// Check if item is a Section or InsightResult before accessing values
				res[col] = this.getValueByKey(item, col);
			}
			return res;
		});

		if (options.ORDER) {
			result = this.applyOrdering(result, options.ORDER);
		}

		return result;
	}

	// Apply ordering based on the ORDER field in query
	private applyOrdering(data: InsightResult[], order: any): InsightResult[] {
		if (typeof order === "string") {
			// Single string ORDER
			const key = order;
			return data.sort((a, b) => (a[key] > b[key] ? 1 : a[key] < b[key] ? -1 : 0));
		} else if (typeof order === "object") {
			// Multi-key ORDER
			if (!order.keys || !Array.isArray(order.keys) || order.keys.length === 0) {
				// Throw error if `keys` is missing or not an array or empty
				throw new InsightError("ORDER must contain a non-empty 'keys' array.");
			}

			const keys = order.keys;
			const dir = order.dir === "DOWN" ? -1 : 1; // Default to "UP" if `dir` is undefined

			return data.sort((a, b) => {
				for (const key of keys) {
					if (a[key] > b[key]) {
						return dir;
					}
					if (a[key] < b[key]) {
						return -dir;
					}
				}
				return 0;
			});
		} else {
			throw new InsightError("Invalid ORDER format.");
		}
	}
}

import { InsightError } from "./IInsightFacade";

export default class QuerySingleIdChecker {
	// Ensure the query references a single dataset
	public checkSingleDataset(query: unknown): boolean {
		const datasetIds = new Set<string>();

		// Ensure OPTIONS and COLUMNS exist and are valid
		this.validateOptionsAndColumns(query);

		// Collect dataset IDs throughout the query
		this.collectDatasetIds(query, datasetIds);

		return datasetIds.size === 1;
	}

	// Validate that OPTIONS and COLUMNS are present and correctly formatted
	private validateOptionsAndColumns(query: unknown): void {
		if (
			!query ||
			typeof query !== "object" ||
			!Array.isArray((query as any).OPTIONS?.COLUMNS) ||
			(query as any).OPTIONS.COLUMNS.length === 0
		) {
			throw new InsightError("OPTIONS must contain a non-empty COLUMNS array.");
		}

		// Collect custom APPLY keys if TRANSFORMATIONS exists
		const customApplyKeys = this.getCustomApplyKeys(query);

		// Validate each column in COLUMNS but ignore custom APPLY keys
		for (const column of (query as any).OPTIONS.COLUMNS) {
			// Skip validation for custom APPLY keys without "_"
			if (!customApplyKeys.has(column) && !column.includes("_")) {
				throw new InsightError(`Key does not match dataset ID format or custom APPLY key: ${column}`);
			}
		}
	}

	// Extract custom APPLY keys from TRANSFORMATIONS
	private getCustomApplyKeys(query: unknown): Set<string> {
		const customKeys = new Set<string>();
		const transformations = (query as any).TRANSFORMATIONS;

		if (transformations && Array.isArray(transformations.APPLY)) {
			for (const applyRule of transformations.APPLY) {
				for (const key in applyRule) {
					customKeys.add(key); // Add custom APPLY keys (e.g., overallAvg)
				}
			}
		}
		return customKeys;
	}

	// Recursive function to traverse through the query object and collect dataset IDs
	private collectDatasetIds(obj: any, datasetIds: Set<string>): void {
		if (typeof obj === "object" && obj !== null) {
			for (const key in obj) {
				if (Object.prototype.hasOwnProperty.call(obj, key)) {
					if (key.includes("_")) {
						this.extractDatasetIdFromKey(key, datasetIds);
					} else if (key === "APPLY") {
						this.collectDatasetIdsFromApply(obj[key], datasetIds);
					} else if (typeof obj[key] === "object" && obj[key] !== null) {
						this.collectDatasetIds(obj[key], datasetIds);
					} else if (Array.isArray(obj[key])) {
						this.collectDatasetIdsFromArray(obj[key], datasetIds);
					}
				}
			}
		}
	}

	// Extract and add dataset ID from a key that contains "_"
	private extractDatasetIdFromKey(key: string, datasetIds: Set<string>): void {
		const datasetId = key.split("_")[0];
		datasetIds.add(datasetId);
	}

	// Collect dataset IDs from an array of objects
	private collectDatasetIdsFromArray(array: any[], datasetIds: Set<string>): void {
		for (const item of array) {
			if (typeof item === "object" && item !== null) {
				this.collectDatasetIds(item, datasetIds);
			}
		}
	}

	// Special handling to collect dataset IDs from APPLY rules
	private collectDatasetIdsFromApply(applyRules: any[], datasetIds: Set<string>): void {
		for (const applyRule of applyRules) {
			for (const operation in applyRule) {
				const applyObj = applyRule[operation];
				for (const applyKey in applyObj) {
					if (applyObj[applyKey].includes("_")) {
						// Only consider fields with "_"
						const datasetId = applyObj[applyKey].split("_")[0];
						datasetIds.add(datasetId);
					}
				}
			}
		}
	}
}

import { InsightError, InsightResult } from "./IInsightFacade";
import DatasetManager from "./DatasetManager";
import Room from "./Room";
import Decimal from "decimal.js";

type DatasetEntry = Room;

const DECIMAL_PRECISION = 2;

export default class RoomQueryRunner {
	private datasetMap: Map<string, DatasetEntry[]>;
	private datasetId!: string;
	private groupedData: Map<string, Room[]>;

	constructor(datasetManager: DatasetManager) {
		this.datasetMap = datasetManager.getDatasets() as Map<string, Room[]>;
		this.groupedData = new Map<string, Room[]>();
	}

	// Execute the query and return the results
	// Execute the query and return the results
	public async execute(query: any): Promise<InsightResult[]> {
		this.datasetId = this.extractDatasetId(query);

		if (!this.datasetMap.has(this.datasetId)) {
			throw new InsightError(`Dataset with id "${this.datasetId}" does not exist.`);
		}

		// Step 1: Apply filters to get the filtered data
		const dataset = this.datasetMap.get(this.datasetId) as Room[];
		const filteredData = this.applyFilters(dataset, query.WHERE);

		// Step 2: Perform transformations only on the filtered data
		const transformedData: InsightResult[] = query.TRANSFORMATIONS
			? this.applyTransformations(filteredData, query.TRANSFORMATIONS) // Pass filteredData here
			: (filteredData as unknown as InsightResult[]);

		// Validate columns against GROUP and APPLY if TRANSFORMATIONS exist
		if (query.TRANSFORMATIONS) {
			const groupKeys = new Set(query.TRANSFORMATIONS.GROUP);
			const applyKeys = new Set(query.TRANSFORMATIONS.APPLY.map((applyRule: any) => Object.keys(applyRule)[0]));
			for (const col of query.OPTIONS.COLUMNS) {
				if (!groupKeys.has(col) && !applyKeys.has(col)) {
					throw new InsightError(`Key ${col} in COLUMNS must be in GROUP or APPLY when TRANSFORMATIONS`);
				}
			}
		}

		return this.applyOptions(transformedData, query.OPTIONS);
	}

	private extractDatasetId(query: any): string {
		for (const col of query.OPTIONS.COLUMNS) {
			if (col.includes("_")) {
				return col.split("_")[0];
			}
		}
		throw new InsightError("Could not extract dataset ID from COLUMNS.");
	}

	private applyFilters(data: Room[], where: any): Room[] {
		if (Object.keys(where).length === 0) {
			return data;
		}
		const filterFunction = this.createFilterFunction(where);
		return data.filter(filterFunction);
	}

	private createFilterFunction(where: any): (item: Room) => boolean {
		if (where.AND) {
			return this.handleAnd(where.AND);
		}
		if (where.OR) {
			return this.handleOr(where.OR);
		}
		if (where.NOT) {
			return this.handleNot(where.NOT);
		}
		if (where.GT || where.LT || where.EQ) {
			return this.handleComparison(where);
		}
		if (where.IS) {
			return this.handleIs(where.IS);
		}
		throw new InsightError("Invalid WHERE condition.");
	}

	private handleAnd(andConditions: any[]): (item: Room) => boolean {
		const subFilters = andConditions.map((condition) => this.createFilterFunction(condition));
		return (item: Room) => subFilters.every((filterFunc) => filterFunc(item));
	}

	private handleOr(orConditions: any[]): (item: Room) => boolean {
		const subFilters = orConditions.map((condition) => this.createFilterFunction(condition));
		return (item: Room) => subFilters.some((filterFunc) => filterFunc(item));
	}

	private handleNot(notCondition: any): (item: Room) => boolean {
		const subFilter = this.createFilterFunction(notCondition);
		return (item: Room) => !subFilter(item);
	}

	private handleComparison(where: any): (item: Room) => boolean {
		const operator = where.GT ? "GT" : where.LT ? "LT" : "EQ";
		const comparison = where[operator];
		const key = Object.keys(comparison)[0];
		const value = comparison[key];

		const id = key.split("_")[0];
		if (id !== this.datasetId) {
			throw new InsightError(`Key does not match dataset ID: ${key}`);
		}

		return (item: Room) => {
			const itemValue = this.getValueByKey(item, key);

			if (typeof itemValue !== "number" || typeof value !== "number") {
				throw new InsightError("Comparison requires numeric values.");
			}

			if (operator === "GT") {
				return itemValue > value;
			}
			if (operator === "LT") {
				return itemValue < value;
			}
			return itemValue === value;
		};
	}

	private handleIs(isCondition: any): (item: Room) => boolean {
		const key = Object.keys(isCondition)[0];
		const value = isCondition[key];
		const id = key.split("_")[0];

		// Validate dataset ID consistency
		if (id !== this.datasetId) {
			throw new InsightError(`Key does not match dataset ID: ${key}`);
		}

		// Ensure IS operator value is a string
		if (typeof value !== "string") {
			throw new InsightError("IS operator requires a string value.");
		}

		// Build regex to handle wildcard patterns correctly
		const regexPattern = this.buildRegexPattern(value);
		const regex = new RegExp(regexPattern);

		return (item: Room) => {
			const itemValue = this.getValueByKey(item, key);

			if (typeof itemValue !== "string") {
				throw new InsightError("IS operator requires string fields.");
			}

			// Apply the regex to test for wildcard matching
			return regex.test(itemValue);
		};
	}

	private buildRegexPattern(value: string): string {
		const specialChars = /[.+?^${}()|[\]\\]/g;
		const escapedValue = value.replace(specialChars, "\\$&"); // Escape special characters except for *
		return `^${escapedValue.replace(/\*/g, ".*")}$`; // Replace * with .*
	}

	private getValueByKey(item: Room | InsightResult, key: string): any {
		// If item is already an InsightResult, return the value directly
		if (!(item instanceof Room)) {
			return item[key];
		}

		// Split the key to get the dataset identifier and field
		const [id, field] = key.split("_");

		// Verify the dataset ID matches
		if (id !== this.datasetId) {
			throw new InsightError(`Key does not match dataset ID: ${key}`);
		}

		// Access fields based on the field name
		switch (field) {
			case "fullname":
				return String(item.getFullname());
			case "shortname":
				return String(item.getShortname());
			case "number":
				return String(item.getNumber());
			case "name":
				return String(item.getName());
			case "address":
				return String(item.getAddress());
			case "type":
				return String(item.getType());
			case "furniture":
				return String(item.getFurniture());
			case "seats":
				return item.getSeats() !== undefined ? item.getSeats() : undefined;
			case "lat":
				return item.getLat();
			case "lon":
				return item.getLon();
			case "href": // Added handling for rooms_href
				return String(item.getHref());
			default:
				// Handle any unexpected field by throwing an error or returning undefined
				throw new InsightError(`Invalid or unexpected column: ${key}`);
		}
	}

	private applyTransformations(data: Room[], transformations: any): InsightResult[] {
		const groupKeys = transformations.GROUP;

		// Ensure groupBy is called only on the filtered data passed as 'data'
		this.groupedData = this.groupBy(data, groupKeys);

		return this.applyAggregations(transformations.APPLY, groupKeys);
	}

	private groupBy(data: Room[], groupKeys: string[]): Map<string, Room[]> {
		const groupedData = new Map<string, Room[]>(); // Temporary map for grouping

		data.forEach((item) => {
			const groupKey = groupKeys.map((key) => this.getValueByKey(item, key)).join("_");
			if (!groupedData.has(groupKey)) {
				groupedData.set(groupKey, []);
			}
			groupedData.get(groupKey)!.push(item);
		});

		return groupedData; // Return the local grouped data map
	}

	private applyAggregations(applyRules: any[], groupKeys: string[]): InsightResult[] {
		const results: InsightResult[] = [];

		this.groupedData.forEach((group) => {
			const result: any = {};

			// Set group key values in the result
			groupKeys.forEach((key) => {
				result[key] = this.getValueByKey(group[0], key);
			});

			// Apply each rule in APPLY to calculate the aggregation
			for (const rule of applyRules) {
				const applyKey = Object.keys(rule)[0];
				const applyToken = rule[applyKey];
				const aggregationField = Object.values(applyToken)[0] as string;
				const aggregationType = Object.keys(applyToken)[0] as string;

				result[applyKey] = this.performAggregation(aggregationType, group, aggregationField);
			}

			results.push(result);
		});

		return results;
	}

	// Perform aggregation operations
	private performAggregation(type: string, group: Room[], field: string): number {
		const isNumeric = group.every((item) => {
			const value = this.getValueByKey(item, field);
			return typeof value === "number" && !isNaN(value);
		});

		if (!isNumeric && type !== "COUNT") {
			throw new InsightError(`${type} requires a numeric field`);
		}

		switch (type) {
			case "MAX":
				return Math.max(...group.map((item) => this.getValueByKey(item, field) as number));
			case "MIN":
				return Math.min(...group.map((item) => this.getValueByKey(item, field) as number));
			case "AVG": {
				const total = group.reduce(
					(acc, item) => acc.add(new Decimal(this.getValueByKey(item, field) as number)),
					new Decimal(0)
				);
				const avg = total.toNumber() / group.length;
				return Number(avg.toFixed(DECIMAL_PRECISION));
			}
			case "SUM": {
				const total = group.reduce(
					(acc, item) => acc.add(new Decimal(this.getValueByKey(item, field) as number)),
					new Decimal(0)
				);
				return Number(total.toFixed(DECIMAL_PRECISION));
			}
			case "COUNT":
				return new Set(group.map((item) => this.getValueByKey(item, field))).size;
			default:
				throw new InsightError("Invalid aggregation type");
		}
	}

	private applyOptions(data: Room[] | InsightResult[], options: any): InsightResult[] {
		if (!options.COLUMNS || !Array.isArray(options.COLUMNS)) {
			throw new InsightError("OPTIONS must contain COLUMNS array.");
		}

		const columns: string[] = options.COLUMNS;
		const result = data.map((item) => {
			const res: any = {};
			columns.forEach((col: string) => {
				res[col] = this.getValueByKey(item, col);
			});
			return res;
		});

		if (options.ORDER) {
			return this.applyOrdering(result, options.ORDER);
		}
		return result;
	}

	private applyOrdering(data: InsightResult[], order: any): InsightResult[] {
		if (typeof order === "string") {
			// Single string ORDER
			const key = order;
			return data.sort((a, b) => (a[key] > b[key] ? 1 : a[key] < b[key] ? -1 : 0));
		} else if (typeof order === "object") {
			// Multi-key ORDER
			if (!order.keys || !Array.isArray(order.keys) || order.keys.length === 0) {
				// Throw error if `keys` is missing or not an array or empty
				throw new InsightError("ORDER must contain a non-empty 'keys' array.");
			}

			const keys = order.keys;
			const dir = order.dir === "DOWN" ? -1 : 1; // Default to "UP" if `dir` is undefined

			return data.sort((a, b) => {
				for (const key of keys) {
					if (a[key] > b[key]) {
						return dir;
					}
					if (a[key] < b[key]) {
						return -dir;
					}
				}
				return 0;
			});
		} else {
			throw new InsightError("Invalid ORDER format.");
		}
	}
}
